exec_code.code_text:
try
{
	MODEOBJECT;
}
catch(_no_xml)
{
	MODEOBJECT = null;
}

oData.data.config = new Object();
	oData.data.config.btn_users = true;
	oData.data.config.btn_objectives = false;
	oData.data.config.btn_logs = false;
	oData.data.config.btn_iframe = true;
	oData.data.config.tree_header_label = "Оценка 360";
	oData.data.config.sLogoAction = "none";



for (_c in (MODEOBJECT==null?oData.data.competences.competence:oData.data.competences))
{
	_cPA = curPA.competences.GetOptChildByKey(OptInt(_c.id,0));
	if (_cPA!=undefined)
	{
		_c.mark = _cPA.mark.Value;
		if (_c.HasProperty("indicators"))
		{
			for (_i in (MODEOBJECT==null?_c.indicators.indicator:_c.indicators))
			{
				_iPA = _cPA.indicators.GetOptChildByKey(OptInt(_i.id,0));
				if (_iPA!=undefined)
				{
					_i.mark = _iPA.mark.Value;
				}
			}
		}
	}
}

if (true)
{
	for (_c in (MODEOBJECT==null?oData.data.competences.competence:oData.data.competences))
	{
		_c.editable = true;
		_vr = String(_c.mark);
		_c.mark = new Object();
		_c.mark.value = _vr;
		_c.mark.visible = true;
		_c.mark.editable = false;
		_c.mark.control_type = "string";
		_c.has_comment = false;
		if (_c.HasProperty("indicators"))
		{
			for (_i in (MODEOBJECT==null?_c.indicators.indicator:_c.indicators))
			{
				_i.editable = oData.data.editable;
				_vr1 = String(_i.mark);
				_i.mark = new Object();
				_i.mark.value = String(_vr1);
				_i.mark.scale = _i.scale;
				_i.mark.visible = true;
				_i.mark.editable = oData.data.editable;
				_i.mark.control_type = "radio-col-bottom-plain-none";
				_i.has_comment = false;
			}
		}
	}
}

oData.data.view_type = "accordion";
oData.data.person.title = "Кого оцениваем:";
oData.data.expert.title = "Кто оценивает:";

if (StrContains(curAssessmentAppraise.code,"360_"))
	oData.data.view_type = StrReplace(curAssessmentAppraise.code,"360_","");


oData.data.config.wf_btn_position = "bottom";

oData.data.wfcomment = true;
oData.data.wfcomment = "<b><h3>Уважаемый коллега!</b></h3>
<h4>Перед Вами опросник 360 градусов. </h4>

<b>Опрос проводится анонимно!</b> Просим Вас  быть  предельно откровенным  и объективным. При обработке информации ваши имя и фамилия фиксироваться  не будут. От Ваших ответов зависит корректность дальнейшей программы развития Вашего коллеги!

<p>Заполнение опросника занимает не более 10 минут.
При заполнении опросника отмечайте только те индикаторы, которые вы точно наблюдали в поведении сотрудника. Не стремитесь угадать оценку, которую поставят другие. 
Первое впечатление обычно наиболее верное. Не задерживайтесь слишком долго на одном вопросе. Выбирайте индикаторы поведения, которые по ощущениям отзываются первыми.
<p>Кнопка <b>'Завершить заполнение'</b> появится только после заполнения всех оценок!";
oData.data.wfcomment_title = "Важно знать!";

